import React, { Component } from "react";

import "../style.css";
import {
  BarChart,
  Bar,
  Cell,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  LineChart,
  Line,
} from "recharts";
import Card from "react-bootstrap/esm/Card";

const mydata = [
  {
    description: "first",
    val: [2, 10],
  },
  {
    description: "second",
    val: [5, 6, 7],
  },
];
const CustomTooltip = ({ active, payload, label }) => {
  if (active && payload && payload.length) {
    return (
      <div className="custom-tooltip">
        <p className="label">{`Anomalies found from : ${payload[0].value[0]} until : ${payload[0].value[1]}`}</p>
        <p className="desc">Anything you want can be displayed here.</p>
      </div>
    );
  }

  return null;
};
class AnomalyChart extends Component {
  constructor(props) {
    super(props);
    this.state = { report: this.props.report };
  }
  render() {
    return (
      <React.Fragment>
        <Card>
          <Card.Body>{this.state.report === null}</Card.Body>
        </Card>
        {/* <BarChart
          width={600}
          height={300}
          data={this.props.report}
          layout="vertical"
        >
          <XAxis type="number" />
          <YAxis type="category" dataKey="description" stroke="#8884d8" />
          <Tooltip content={<CustomTooltip />} />
          <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
          <Bar dataKey="timeStep" fill="#8884d8" barSize={30} />
        </BarChart> */}
      </React.Fragment>
    );
  }
}

export default AnomalyChart;
