import React, { Component } from "react";
import Row from "react-bootstrap/Row";
import InputForm from "./inputForm";
import AnomalyChart from "./anomalyChart";
import "../style.css";

class Controller extends Component {
  constructor(props) {
    super(props);
    this.state = { report: null };
  }
  moveAnomalies = (report) => {
    console.log(report);
    this.setState({ gotOutput: report });
  };
  render() {
    return (
      <React.Fragment>
        <Row>
          <InputForm parentCallback={this.moveAnomalies} />
        </Row>

        {this.state.gotOutput && (
          <Row>
            <AnomalyChart reportFromController={this.state.report} />
          </Row>
        )}
      </React.Fragment>
    );
  }
}

export default Controller;
