import React, { Component } from "react";

class DragAndDrop extends Component {
  state = {
    count: 1,
  };
  render() {
    return (
      <div>
        <span className="badge rounded-pill bg-primary m-2">
          {this.formatCount()}
        </span>
      </div>
    );
  }
  formatCount() {
    const { count } = this.state;
    return count === 0 ? "Zero" : count;
  }
}

export default DragAndDrop;
