import React, { Component } from "react";

import "../style.css";
import {
  BarChart,
  Bar,
  Cell,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  LineChart,
  Line,
} from "recharts";

const mydata = [
  {
    description: "first",
    val: [2, 3, 4, 10],
  },
  {
    description: "second",
    val: [5, 6, 7],
  },
];
const CustomTooltip = ({ active, payload, label }) => {
  if (active && payload && payload.length) {
    return (
      <div className="custom-tooltip">
        <p className="label">{`${label} : ${payload[0].value[0]}`}</p>
        <p className="label">{`${label} : ${payload[0].value[0]}`}</p>
        <p className="desc">Anything you want can be displayed here.</p>
      </div>
    );
  }

  return null;
};
class AnomalyChart extends Component {
  render() {
    return (
      <BarChart width={600} height={300} data={mydata} layout="vertical">
        <XAxis type="number" />
        <YAxis type="category" dataKey="description" stroke="#8884d8" />
        <Tooltip content={<CustomTooltip />} />
        <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
        <Bar dataKey="val" fill="#8884d8" barSize={30} />
      </BarChart>
    );
  }
}

export default AnomalyChart;
