import React, { useState } from "react";

import "../style.css";
import {
  BarChart,
  Bar,
  Cell,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer,
  LineChart,
  Line,
} from "recharts";

const CustomizedTooltip = ({ active, payload, label }) => {
  if (active && payload && payload.length) {
    return (
      <div className="custom-tooltip">
        <p className="label">{`Anomalies found from : ${payload[0].value[0]} until : ${payload[0].value[1]}`}</p>
        <p className="desc">Anything you want can be displayed here.</p>
      </div>
    );
  }
  return null;
};
const CustomizedLabelAxis = ({ x, y, payload }) => {
  const splitted = payload.value.split("~~");
  return (
    <g transform={`translate(${x},${y})`}>
      <text x={0} y={0} textAnchor="end" fill="#666">
        <tspan>{splitted[0]}</tspan>
        <tspan x={0} dy="20">
          {splitted[1]}
        </tspan>
      </text>
    </g>
  );
};
function measureText(description) {
  const ctx = document.createElement("canvas").getContext("2d");
  return ctx.measureText(description).width;
}
const AnomalyChart = (props) => {
  const [data] = useState(props.data);
  let leftMargin = 0;
  for (const value of data) {
    const textWidth = measureText(data.description);
    if (textWidth > leftMargin) {
      leftMargin = textWidth;
    }
  }
  leftMargin = Math.max(0, leftMargin - 50);
  return (
    <ResponsiveContainer className="chart" width="50%" height={400}>
      <BarChart
        data={data}
        margin={{
          top: 5,
          right: 30,
          left: leftMargin,
          bottom: 5,
        }}
        layout="vertical"
      >
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis type="number" />
        <YAxis
          type="category"
          dataKey="description"
          tick={<CustomizedLabelAxis />}
        />
        <Tooltip content={<CustomizedTooltip />} />
        <Legend />
        <Bar dataKey="timeStep" fill="#8884d8" />
      </BarChart>
      {/* <BarChart
          width={600}
          height={300}
          data={this.props.report}
          layout="vertical"
        >
          <XAxis type="number" />
          <YAxis type="category" dataKey="description" stroke="#8884d8" />
          <Tooltip content={<CustomTooltip />} />
          <CartesianGrid stroke="#ccc" strokeDasharray="5 5" />
          <Bar dataKey="timeStep" fill="#8884d8" barSize={30} />
        </BarChart> */}
    </ResponsiveContainer>
  );
};

export default AnomalyChart;
