import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import FormFile from "react-bootstrap/esm/FormFile";
import FormFileInput from "react-bootstrap/esm/FormFileInput";

class DragAndDrop extends Component {
  state = {
    detectorType: "",
    learnCsvFile: null,
    name: "",
  };
  nameChanged = (event) => {
    this.setState({ name: event.target.value });
  };
  selectionChanged = (event) => {
    this.setState({ detectorType: event.target.value });
  };
  fileSelected = (event) => {
    let fileProp = event.target.name;
    let val = event.target.files[0].name;
    this.setState({ learnCsvFile: val });
  };
  render() {
    return (
      <Form className="m-3 items" style={{ textAlign: "center" }}>
        <Row>
          <Col xs="auto" className="my-1">
            <Form.Control
              as="select"
              className="mr-sm-2"
              onChange={this.selectionChanged}
              custom
            >
              <option value="">Choose Algorithem...</option>
              <option value="regression">Regression</option>
              <option value="hybrid">Hybrid</option>
            </Form.Control>
          </Col>
          <Col>
            <FormFileInput>das</FormFileInput>
            <div class="form-group mb-3">
              <div class="custom-file">
                <input
                  type="file"
                  id="file_input"
                  class="custom-file-input"
                  onInput={this.fileSelected}
                />
                <label
                  for="file_input"
                  id="file_input_label"
                  class="custom-file-label"
                >
                  Select file
                </label>
              </div>
            </div>
          </Col>
          <Col>
            <Form.File
              id="custom-file-translate-scss"
              label="Custom file input"
              custom
            >
              <Form.File.Input isValid />
              <Form.File.Label placeholder="dad" data-browse="Browse..">
                Upload Anomaly CSV file
              </Form.File.Label>
              <Form.Control.Feedback type="valid">
                You did it!
              </Form.Control.Feedback>
            </Form.File>
          </Col>
          <Col>
            <Button type="submit">Submit</Button>
          </Col>
        </Row>
        <Row>
          <input type="text" onChange={this.nameChanged} />
          <p>Hello {this.state.name}</p>
          <p>DT Type {this.state.detectorType}</p>
          <p>DT file name {this.state.learnCsvFile}</p>
        </Row>
      </Form>
    );
  }
  formatCount() {
    const { count } = this.state;
    return count === 0 ? "Zero" : count;
  }
}

export default DragAndDrop;
