import React, { Component } from "react";
import Button from "react-bootstrap/Button";
import Row from "react-bootstrap/Row";
import Col from "react-bootstrap/Col";
import Form from "react-bootstrap/Form";
import Card from "react-bootstrap/Card";

import "../style.css";
import axios from "axios";
// an initial state to fall back to
const initialState = {
  detectorType: "",
  learnFileName: "Please Upload Learn CSV",
  anomalyFileName: "Please Upload Anomaly CSV",
  learnFile: null, // learn file
  anomalyFile: null, // test file
  anomalyReport: null,
  errors: { learnFileName: false, anomalyFileName: false },
};

class InputForm extends Component {
  state = initialState;

  selectionChanged = (event) => {
    this.setState({ detectorType: event.target.value });
  };

  csvToJson() {}
  // need to add some comments here..
  fileSelected = (event) => {
    let file = event.target.files[0];
    let file_type = file.type; //getting selected file type
    let valid_extensions = ["application/vnd.ms-excel", "csv"]; //adding some valid csv extensions in array
    let fileNameProperty = event.target.name;
    if (valid_extensions.includes(file_type)) {
      let fileName = event.target.files[0].name;
      this.setState({ [fileNameProperty]: fileName });
      let fileDataProperty = fileNameProperty.replace("Name", "");
      let fileReader = new FileReader(); //creating new FileReader object
      fileReader.readAsText(file, "UTF-8");
      fileReader.onload = () => {
        var lines = fileReader.result.split(/\r\n|\r|\n/g); //split by new line depending on system
        var result = [];
        var headers = lines[0].split(",");
        var len = lines.length;
        for (var i = 0; i < headers.length; i++) {
          var obj = {};
          var values = [];
          for (var j = 1; j < len - 1; j++) {
            var currentline = lines[j].split(",");
            values.push(parseFloat(currentline[i]));
          }
          obj[headers[i]] = values;
          result.push(obj);
        }
        var json = JSON.stringify(result); //JSON
        json = json.replaceAll("{", "");
        json = json.replaceAll("}", "");
        json = json.substring(1, json.length - 1); // remove wrapping []
        json = "{" + json + "}";
        this.setState({ [fileDataProperty]: json });
      };
    } else {
      // invalid type - raise error
      const { errors } = { ...this.state };
      const currentState = errors;
      const { name } = event.target;
      currentState[name] = true;

      this.setState({ errors: currentState });
      // this.setState({ [fileNameProperty]: fileName });

      console.log(this.state);
    }
  };

  setDefaultState() {
    this.setState(initialState);
  }
  checkFormValidation() {
    const { detectorType, learnFileName, anomalyFileName } = this.state;
    const newErrors = {};
    // if (detectorType === initialState.detectorType )
    //   newErrors.detectorType = 'please select an Algorithem'
    // if (learnFileName == initialState.learnFileName || learnFileName)
    //   newErrors.learnFileName = 'please select an Algorithem'
  }

  submitHandler = (event) => {
    event.preventDefault();
    alert("submitted files!!!!");
    axios
      .post("/detect", {
        detectorType: this.state.detectorType,
        learn: this.state.learnFile,
        anomaly: this.state.anomalyFile,
      })
      .then((response) => {
        this.setState({ anomalyReport: response.data.report });
      })
      .catch((err) => console.log(err));
    //set the form back to default
    this.setDefaultState();
  };

  render() {
    return (
      <Form
        className="m-3 items mainForm needs-validation"
        noValidate
        onSubmit={this.submitHandler}
      >
        <Row>
          <Col xs="auto">
            <Form.Control
              as="select"
              className="mr-sm-2"
              onChange={this.selectionChanged}
              custom
              value={this.state.detectorType}
            >
              <option value="">Choose Algorithem...</option>
              <option value="regression">Regression</option>
              <option value="hybrid">Hybrid</option>
            </Form.Control>
          </Col>
          <Col>
            <Form.Group>
              <Form.File
                className="inputFile"
                id="custom-file"
                label={this.state.learnFileName}
                onChange={this.fileSelected}
                name="learnFileName"
                custom
                isInvalid={this.state.errors.learnFileName}
              ></Form.File>
            </Form.Group>
          </Col>
          <Col>
            <Form.Group>
              <Form.File
                className="inputFile"
                id="custom-file"
                label={this.state.anomalyFileName}
                name="anomalyFileName"
                onInput={this.fileSelected}
                custom
                isInvalid={this.state.errors.anomalyFileName}
              ></Form.File>
            </Form.Group>
          </Col>
          <Col>
            <Button type="submit">Submit</Button>
          </Col>
        </Row>
        <Row>
          <Card>
            <Card.Body>
              {/* prints report iff not null */}
              {this.state.anomalyReport
                ? JSON.stringify(this.state.anomalyReport)
                : ""}
            </Card.Body>
          </Card>
        </Row>
      </Form>
    );
  }
}

export default InputForm;
